// Helper function to get translated questions with fallback to Italian
export function getTranslatedQuestions(language: string, category: string): Question[] {
  // First try to get translated questions
  const translatedQuestions = TRANSLATED_QUESTIONS[language]?.[category];
  if (translatedQuestions && translatedQuestions.length > 0) {
    return translatedQuestions;
  }
  
  // Fallback to Italian questions from the main QUESTIONS object
  // This import would need to be dynamic or we need to structure differently
  return [];
}
