// Helper function to get questions for a language, falling back to Italian
export const getTranslatedQuestions = (language: string, category: string): Question[] => {
  const langQuestions = TRANSLATED_QUESTIONS[language];
  if (langQuestions && langQuestions[category]) {
    return langQuestions[category];
  }
  // Fallback to Italian
  return TRANSLATED_QUESTIONS.it[category] || [];
};
